mapper.select:
SELECT
FROM admin_metadata_collection amc LEFT JOIN admin_metadata_collection_metadata amcm ON amc.id_ = amcm.metadata_collection_id LEFT JOIN admin_metadata am ON amcm.metadata_id = am.id_ WHERE amc.enable_ = 1 AND am.enable_ = 1
AND amc.id_ = #{id}
and amc.en_name = #{enName}
ORDER BY amc.en_name, amc.mdc_version, amcm.sort_no
